import React from "react";
import { Link } from "react-router-dom";
import "./App.css";
import { NavLink } from "react-router-dom";
import Sidebar from "./Sidebar";

class Navbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showBuilderSidebar: false,
      showUsersSidebar: false,
    };
  }

  toggleBuilderSidebar = () => {
    this.setState((prevState) => ({
      showBuilderSidebar: !prevState.showBuilderSidebar,
    }));
  };

  toggleUsersSidebar = () => {
    this.setState((prevState) => ({
      showUsersSidebar: !prevState.showUsersSidebar,
    }));
  };

  render() {
    return (
      <div>
        <div className="nav">
          <nav>
            <ul>
              <div className="title">
                <li>Cerner Learning</li>
              </div>
              <li>
                <NavLink to="/journeys" onClick={this.toggleBuilderSidebar}>
                  Builder
                </NavLink>
              </li>
              <li>
                <NavLink to="/users" onClick={this.toggleUsersSidebar}>
                  User
                </NavLink>
              </li>
              <li>
                <NavLink to="/reports" style={{ pointerEvents: "none" }}>
                  Reports
                </NavLink>
              </li>
              <div className="logout">
                <li>
                  <Link to="/">Logout</Link>
                </li>
              </div>
            </ul>
          </nav>
        </div>

        {this.state.showBuilderSidebar && <Sidebar type="builder" />}
        {this.state.showUsersSidebar && <Sidebar type="users" />}
      </div>
    );
  }
}

export default Navbar;
