import React, { useState } from "react";
import { Link } from "react-router-dom";
import './App.css';
import { NavLink } from "react-router-dom";
import SidebarBuilder from "./SidebarBuilder";
import SidebarUsers from "./SidebarUsers";

function Navbar() {
  const [activeMenu, setActiveMenu] = useState("builder");

  const handleMenuClick = (menu) => {
    setActiveMenu(menu);
  };

  return (
    <div className="nav">
      <nav>
        <ul>
          <div className="title">
            <li>Cerner Learning</li>
          </div>
          <li>
            <NavLink
              to="/journeys"
              onClick={() => handleMenuClick("builder")}
              isActive={() => activeMenu === "builder"}
            >
              Builder
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/users"
              onClick={() => handleMenuClick("users")}
              isActive={() => activeMenu === "users"}
            >
              User
            </NavLink>
          </li>
          <li>
            <NavLink to="/reports" style={{ pointerEvents: "none" }}>
              Reports
            </NavLink>
          </li>
          <div className="logout">
            <li>
              <Link to="/">Logout</Link>
            </li>
          </div>
        </ul>
      </nav>

      {activeMenu === "builder" && <SidebarBuilder />}
      {activeMenu === "users" && <SidebarUsers />}
    </div>
  );
}

export default Navbar;
