import React from "react";
import { Link } from "react-router-dom";
import "./App.css";

class Sidebar extends React.Component {
  state = {
    showJourneys: true,
  };

  handleJourneysClick = () => {
    this.setState({ showJourneys: true });
  };

  handleUsersClick = () => {
    this.setState({ showJourneys: false });
  };

  render() {
    return (
      <div className="sidebar">
        <div className="nav">
          <ul>
            <li onClick={this.handleJourneysClick}>
              <Link to="#">Journeys</Link>
            </li>
            <li onClick={this.handleUsersClick}>
              <Link to="#">Users</Link>
            </li>
          </ul>
        </div>

        {this.state.showJourneys && (
          <ul>
            <li>
              <Link to="/journeys">Journey 1</Link>
            </li>
            <li>
              <Link to="/journeys">Journey 2</Link>
            </li>
            <li>
              <Link to="/journeys">Journey 3</Link>
            </li>
          </ul>
        )}

        {!this.state.showJourneys && (
          <ul>
            <li>
              <Link to="/users">User 1</Link>
            </li>
            <li>
              <Link to="/users">User 2</Link>
            </li>
            <li>
              <Link to="/users">User 3</Link>
            </li>
          </ul>
        )}
      </div>
    );
  }
}

export default Sidebar;
