import React from "react";
import { Link, NavLink } from "react-router-dom";
import './App.css'

class Navbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeLink: ''
    };
  }

  setActiveLink = (link) => {
    this.setState({ activeLink: link });
  }

  render() {
    return (
      <div className="nav">
        <nav>
          <ul>
            <div className="title">
              <li>Cerner Learning</li>
            </div>
            <li>
              <NavLink to="/journeys" onClick={() => this.setActiveLink('journeys')} activeClassName="active">Builder</NavLink>
            </li>
            <li>
              <NavLink to="/users" onClick={() => this.setActiveLink('users')} activeClassName="active">User</NavLink>
            </li>
            <li>
              <NavLink to="/reports" style={{ pointerEvents: "none" }}>Reports</NavLink>
            </li>
            <div className="logout">
              <li>
                <Link to="/">Logout</Link>
              </li>
            </div>
          </ul>
        </nav>
        {this.state.activeLink === 'journeys' &&
          <SidebarJourneys />
        }
        {this.state.activeLink === 'users' &&
          <SidebarUsers />
        }
      </div>
    );
  }
}

class SidebarJourneys extends React.Component {
  render() {
    return (
      <div className="sidebar">
        <div className="nav">
          <ul>
            <li>
              <Link to="/journeys">Journeys</Link>
            </li>
            <li>
              <Link to="">Activities</Link>
            </li>
            <li>
              <Link to="">Events</Link>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}

class SidebarUsers extends React.Component {
  render() {
    return (
      <div className="sidebar">
        <div className="nav">
          <ul>
            <li>
              <Link to="/users">Users</Link>
            </li>
            <li>
              <Link to="">Groups</Link>
            </li>
            <li>
              <Link to="">Session Rosters</Link>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}

export default Navbar;
