const autoprefixer = require('autoprefixer');
const extractTextPlugin = require('extract-text-webpack-plugin');
const path = require('path');

const modulePaths = Object.keys(require("./package.json")["dependencies"])
  .reduce(function(paths, module) { return paths.concat(require(module).includePaths) }, []);

module.exports = {
  entry: {
    application: path.join(__dirname, 'src', 'webpack')
  },
  output: {
    path: path.join(__dirname, 'dist'),
    filename: path.join('js', 'terra', '[name].js')
  },
  resolve: {
    root: modulePaths
  },
  module: {
    loaders: [
      {
        test: /\.scss$/,
        loader: extractTextPlugin.extract('style', 'css!postcss!sass'),
      },
    ],
  },
  postcss: [autoprefixer],
  sassLoader: {
    includePaths: modulePaths
  },
  plugins: [new extractTextPlugin(path.join('css', 'terra', '[name].css'))]
};
